tcpdump src port 80 -r /tmp/server_fe.pcap  -w /tmp/server_fe_rx.pcap
tcpdump dst port 80 -r /tmp/server_fe.pcap  -w /tmp/server_fe_tx.pcap

tshark -q -z io,stat,1500,tcp,\ "COUNT(tcp.analysis.out_of_order) tcp.analysis.out_of_order",\ "COUNT(tcp.analysis.retransmission) tcp.analysis.retransmission",\ "COUNT(tcp.analysis.duplicate_ack)tcp.analysis.duplicate_ack",\ "COUNT(tcp.analysis.lost_segment) tcp.analysis.lost_segment",\ "COUNT(tcp.analysis.fast_retransmission) tcp.analysis.fast_retransmission",\ "COUNT(tcp.flags) tcp.flags eq 0x02",\ "COUNT(tcp.flags) tcp.flags eq 0x12",\ "COUNT(tcp.flags)(tcp.flags eq 0x10) && (tcp.len eq 0)",\ "COUNT(tcp.flags) tcp.flags eq 0x04",\ "COUNT(tcp.flags) tcp.flags eq 0x14",\ "COUNT(tcp.flags) tcp.flags eq 0x01",\ "COUNT(tcp.flags) tcp.flags eq 0x11",\ "SUM(tcp.len) tcp.len",\ "MIN(tcp.analysis.ack_rtt) tcp.analysis.ack_rtt" -r /tmp/server_fe.pcap
echo "DUMPING------------------> TX "
tshark -q -z io,stat,1500,tcp,\ "COUNT(tcp.analysis.out_of_order) tcp.analysis.out_of_order",\ "COUNT(tcp.analysis.retransmission) tcp.analysis.retransmission",\ "COUNT(tcp.analysis.duplicate_ack)tcp.analysis.duplicate_ack",\ "COUNT(tcp.analysis.lost_segment) tcp.analysis.lost_segment",\ "COUNT(tcp.analysis.fast_retransmission) tcp.analysis.fast_retransmission",\ "COUNT(tcp.flags) tcp.flags eq 0x02",\ "COUNT(tcp.flags) tcp.flags eq 0x12",\ "COUNT(tcp.flags)(tcp.flags eq 0x10) && (tcp.len eq 0)",\ "COUNT(tcp.flags) tcp.flags eq 0x04",\ "COUNT(tcp.flags) tcp.flags eq 0x14",\ "COUNT(tcp.flags) tcp.flags eq 0x01",\ "COUNT(tcp.flags) tcp.flags eq 0x11",\ "SUM(tcp.len) tcp.len",\ "MIN(tcp.analysis.ack_rtt) tcp.analysis.ack_rtt" -r /tmp/server_fe_tx.pcap
echo "DUMPING------------------> RX "
tshark -q -z io,stat,1500,tcp,\ "COUNT(tcp.analysis.out_of_order) tcp.analysis.out_of_order",\ "COUNT(tcp.analysis.retransmission) tcp.analysis.retransmission",\ "COUNT(tcp.analysis.duplicate_ack)tcp.analysis.duplicate_ack",\ "COUNT(tcp.analysis.lost_segment) tcp.analysis.lost_segment",\ "COUNT(tcp.analysis.fast_retransmission) tcp.analysis.fast_retransmission",\ "COUNT(tcp.flags) tcp.flags eq 0x02",\ "COUNT(tcp.flags) tcp.flags eq 0x12",\ "COUNT(tcp.flags)(tcp.flags eq 0x10) && (tcp.len eq 0)",\ "COUNT(tcp.flags) tcp.flags eq 0x04",\ "COUNT(tcp.flags) tcp.flags eq 0x14",\ "COUNT(tcp.flags) tcp.flags eq 0x01",\ "COUNT(tcp.flags) tcp.flags eq 0x11",\ "SUM(tcp.len) tcp.len",\ "MIN(tcp.analysis.ack_rtt) tcp.analysis.ack_rtt" -r /tmp/server_fe_rx.pcap
